// === CONTROLLO COLLISIONE DI UN TILE ===
export function isCollidingTile(x, y) {
    const col = Math.floor(x / tileSize);
    const row = Math.floor(y / tileSize);

    if (
        row < 0 ||
        row >= currentMap.tiles.length ||
        col < 0 ||
        col >= currentMap.tiles[0].length
    ) return false;

    return currentMap.tiles[row][col] === 1;
}

// === CONTROLLO COLLISIONE DEL PLAYER ===
export function collision(player, enemy) {
    const playerleft = player.hitbox.x  ;
    const playerright = player.hitbox.x + player.hitbox.width;
    const playertop = player.hitbox.y;
    const playerbottom = player.hitbox.y + player.hitbox.height;

    const topLeft = isCollidingTile(playerleft, playertop);
    const topRight = isCollidingTile(playerright, playertop);
    const bottomLeft = isCollidingTile(playerleft, playerbottom);
    const bottomRight = isCollidingTile(playerright, playerbottom);

    if (topLeft || topRight || bottomLeft || bottomRight) {
        // Corregge posizione “spingendo indietro” il player
        if (player.speedx > 0) player.x -= player.speedx;
        if (player.speedx < 0) player.x -= player.speedx;
        if (player.speedy > 0) player.y -= player.speedy;
        if (player.speedy < 0) player.y -= player.speedy;

        player.speedx = 0;
        player.speedy = 0;
    }
}

function getCurrentLocartion() {
   
        const newMap = JSON.parse(localStorage.getItem("current location"))
    return newMap
}

let currentMapIndex = getCurrentLocartion() || 0;
let currentMap = maps[currentMapIndex];


function loadMap(index) {
    currentMapIndex = index;
    currentMap = maps[currentMapIndex]; 
    
    
}



export let currentMapData = null;

export function setMapData(data) {
    currentMapData = data;
}


function getTilesetForGid(tilesets, gid){
    let selected = null;
    for(const ts of tilesets){
        if(gid >= ts.firstgid) selected = ts;
    }
    return selected;
}

function drawFullMap( ctx, startCol, endCol, startRow, endRow){
    if(!currentMapData) return;

    const {map, tilesets} = currentMapData;
    
    for(const layer of map.layers){
        if (layer.type !== "tilelayer") continue;

        const {data, width} = layer;

        for(let row = startRow; row <= endRow; row++){
            for(let col = startCol; col <= endCol; col++){

           const i = row * width + col ;
            const gid = data[i]
            if(gid === 0 ) continue //se il tile è vuoto vai avanti

            const tileset = getTilesetForGid(tilesets, gid);
            if (!tileset) continue;

            const localId = gid - tileset.firstgid;

            const sx = (localId % tileset.columns) * tileset.tileW;
            const sy = Math.floor(localId / tileset.columns) * tileset.tileH;

            const dx = (i % width) * tileset.tileW;
            const dy = Math.floor(i / width) * tileset.tileH;

            ctx.drawImage(
                tileset.img,
                sx, sy, tileset.tileW, tileset.tileH,
                dx, dy, tileset.tileW, tileset.tileH
            )
        }}
        
    }
}




    
    function drawMap(ctx, camera) {
          if (!currentMapData) return;
          
        const startCol = Math.floor(camera.x /tileSize)
        const endCol = Math.floor((camera.x + resolution_width)/tileSize)

        const startRow = Math.floor(camera.y/tileSize)
        const endRow = Math.floor((camera.y + resolution_height)/tileSize)
       

         drawFullMap(ctx, startCol, endCol, startRow, endRow )   
    };





export {currentMap, drawMap}